    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        try
        {
            ContactoDeal contactoDeal = new ContactoDeal();
            
            Contactos contactos = new Contactos();
            contactos.setNombre(txtNombre.getText());
            contactos.setAsunto(txtAsunto.getText());
            contactos.setCorreoElectronico(txtMail.getText());
            contactos.setMensaje(txtMensaje.getText());
            //Llamar a una clase de java
            if(contactoDeal.Alta(contactos)){
                 System.out.println("Se agrego correctamente la consulta");
                 txtNombre.setText("");
                 txtAsunto.setText("");
                 txtMail.setText("");
                 txtMensaje.setText("");
                 JOptionPane.showMessageDialog(this, "A la brevedad lo estaremos contactando","Correcto",JOptionPane.INFORMATION_MESSAGE);
            }
            else{
                JOptionPane.showMessageDialog(this, "Por favor intente contactarnos m√°s tarde","Error",JOptionPane.ERROR_MESSAGE);
                System.out.println("Error al agregar la consulta");
            }
            
            /*
            gson-2.2.2.jar
java.net.HttpURLConnection;
            */
        }
        catch(Exception e)
        {
             System.out.println(e);
        }

    }//GEN-LAST:event_btnEnviarActionPerformed

